import React from 'react'
import { Link, useLocation, Route, Routes } from "react-router-dom";

//subpages
import Douglas from './Douglas'
import Mark from './Mark'
import Victor from './Victor'
import Ansari from './Ansari'

const links = [
  {
    number: 1,
    path: '/crew/douglas'
  },
  {
    number: 2,
    path: '/crew/mark'
  },
  {
    number: 3,
    path: '/crew/victor'
  },
  {
    number: 4,
    path: '/crew/ansari'
  }
]

export default function Crew() {
  const location = useLocation();


  return (
    <div className='bg-crewmobile md:bg-crewtablet lg:bg-crewdesktop bg-cover h-[100vh] text-white'>
      <div className='pt-[18%] md:pt-[20%] flex flex-col items-center lg:items-start lg:flex-row'>
        <div className='flex gap-3 text-2xl lg:ml-32'>
          <span className='font-others tracking-widest opacity-50'>02</span>
          <h2 className='font-others tracking-widest'>MEET YOUR CREW</h2>
        </div>

        <div className='absolute bottom-16 left-0'>
          <ul className='flex lg:gap-0 h-16 lg:w-3/5 lg:ml-auto lg:mt-16 items-center'>
              {links.map((nav) => (
                          <Link key={nav.number} className={location.pathname == nav.path ? 'navitem border-b-4 border-white' : 'navitem'} to={nav.path}>
                             {nav.number}
                          </Link>
                      ))}
            </ul>
        </div>
      </div>
    </div>
  )
}
