import React from 'react';
import { Link, useLocation } from "react-router-dom";
import logo from '../assets/shared/logo.svg'

const navItems = [
    {
        number: '00',
        text: 'HOME',
        path: '/'
    },
    {
        number: '01',
        text: 'DESTINATION',
        path: '/destination'
    },
    {
        number:'02',
        text: 'CREW',
        path: '/crew'
    },
    {
        number: '03',
        text: 'TECHNOLOGY',
        path: '/technology'
    },
];

export default function Navbar() {
    const location = useLocation()

  return (
        <nav className='navbar'>
                <div className='px-20'>
                    <img src={logo} alt="logo" />
                </div>
                <span className='h-[1px] w-[30%] absolute left-52 bg-slate-600 z-10'></span>

                <ul className='navlinks'>
                    {navItems.map((nav) => (
                        <Link className={location.pathname == nav.path ? 'navitem border-b-4 border-white' : 'navitem'} to={nav.path}>
                            <span className='font-bold font-others md:hidden lg:block'>{nav.number}</span>
                            {nav.text}
                        </Link>
                    ))}
                </ul>
           
        </nav>
  )
}
