import React from 'react';
import { Link, useLocation } from "react-router-dom";
import logo from '../assets/shared/logo.svg'

const navItems = [
    {
        number: '00',
        text: 'HOME',
        path: '/'
    },
    {
        number: '01',
        text: 'DESTINATION',
        path: '/destination'
    },
    {
        number:'02',
        text: 'CREW',
        path: '/crew'
    },
    {
        number: '03',
        text: 'TECHNOLOGY',
        path: '/technology'
    },
];

export default function Navbar() {
    const location = useLocation()

  return (
        <nav className='navbar'>
                <div className='px-20'>
                    <img src={logo} alt="logo" />
                </div>
                <span className='h-[1px] w-[30%] absolute left-52 bg-slate-600 z-10'></span>
            
                <ul className='navlinks'>
                    <Link to='/' className='navitem border-b-4 border-white'><span className='font-bold'>00</span>HOME</Link>
                    <Link to='/destination' className='navitem' ><span className='font-bold'>01</span>DESTINATION</Link>
                    <Link to='/crew' className='navitem'><span className='font-bold'>02</span>CREW</Link>
                    <Link to='/technology' className='navitem'><span className='font-bold'>03</span>TECHNOLOGY</Link>
                </ul>

                <ul className='navlinks'>
                    {navItems.map((nav) => (
                        <Link className='navitem' to={nav.path}>
                            <span className='font-bold'>{nav.number}</span>
                            {nav.text}
                        </Link>
                    ))}
                </ul>
           
        </nav>
  )
}
