import React from 'react'
import { Link, useLocation, Route, Routes } from "react-router-dom";

export default function Destination(props) {
  const data = props.data;
  const isPending = props.isPending;
  const location = useLocation();

  console.log(data);
  const destinations = [
    {
      text: 'MOON',
      path: '/destination/moon'
    },
    {
      text: 'MARS',
      path: '/destination/mars'
    },
    {
      text: 'EUROPA',
      path: '/destination/europa'
    },
    {
      text: 'TITAN',
      path: '/destination/titan'
    }
  ];
  

  return (
    <div className='bg-destinationmobile md:bg-destinationtablet lg:bg-destinationdesktop bg-cover h-[100vh] text-white'>
        <div className='md:pt-[25%] flex'>
          <div className='flex gap-3 text-2xl ml-30'>
            <span className='font-others tracking-widest'>01</span>
            <h3 className='font-others tracking-widest'>PICK YOUR DESTINATION</h3>
          </div>

          <ul className='flex lg:gap-0 h-28 w-3/5 ml-auto items-center'>
            {destinations.map((nav) => (
                        <Link key={nav.text} className={location.pathname == nav.path ? 'navitem border-b-4 border-white' : 'navitem'} to={nav.path}>
                            {nav.text}
                        </Link>
                    ))}
          </ul>


        </div>
    </div>
  )
}
