import React, { useState } from 'react';
import { Link, useLocation } from "react-router-dom";
import logo from '../assets/shared/logo.svg'

const navItems = [
    {
        number: '00',
        text: 'HOME',
        path: '/'
    },
    {
        number: '01',
        text: 'DESTINATION',
        path: '/destination'
    },
    {
        number:'02',
        text: 'CREW',
        path: '/crew'
    },
    {
        number: '03',
        text: 'TECHNOLOGY',
        path: '/technology'
    },
];

export default function Navbar() {
    const location = useLocation()
    const [openNav, setOpenNav] = useState(false)

  return (
        <nav className='navbar'>
                <div className='px-20'>
                    <img src={logo} alt="logo" />
                </div>
                <span className='h-[1px] w-[30%] absolute left-52 bg-slate-600 z-10 hidden lg:block'></span>

                <ul className='navlinks'>
                    {navItems.map((nav) => (
                        <Link className={location.pathname == nav.path ? 'navitem border-b-4 border-white' : 'navitem'} to={nav.path}>
                            <span className='font-bold font-others md:hidden lg:block'>{nav.number}</span>
                            {nav.text}
                        </Link>
                    ))}
                </ul>

                <div className='h-[100vh] w-2/3 bg-[#7d7979]/[.3] backdrop-blur-md shadow-[0_4px_30px_rgba(0,0,0,0.1)]'>
                    <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                    {navItems.map((nav) => (
                            <Link className='navitem' to={nav.path}>
                                <span className='font-bold font-others'>{nav.number}</span>
                                {nav.text}
                            </Link>
                        ))}
                </div>

                <svg className='ml-auto md:hidden block h-12 w-12 mr-10 cursor-pointer' xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                    <path fill-rule="evenodd" d="M3 5a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 10a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 15a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clip-rule="evenodd" />
                </svg>
           
        </nav>
  )
}
